from app.soapclientbuilder import SoapClientBuilder

class TestSoapClientBuilder:
    
    def test_build_request_body(self):
        # Given
        msisdn = '414000114'
        probe_minutiae = ''
        candidate_template = ''
        builder = SoapClientBuilder()

        # When
        soap_request_body = builder.build_request_body(msisdn, probe_minutiae, candidate_template)

        # Then
        print(soap_request_body)
        assert isinstance(soap_request_body, str)
    
    def test_send_request(self):
        # Given
        builder = SoapClientBuilder()
        soap_body = '<soapenv:Envelope xmlns:soapenv="http://schemas.xmlsoap.org/soap/envelope/" xmlns:end="http://endpoint.tlc.com/"><soapenv:Header/><soapenv:Body><end:verifier><!--Optional:--><parameter><msisdn>414000114</msisdn><probeMinutiae></probeMinutiae><candidateTemplate></candidateTemplate></parameter></end:verifier></soapenv:Body></soapenv:Envelope>'

        # When
        api_response = builder.send_request(soap_body)

        # Then
        print(api_response)
        assert isinstance(api_response, str)
